INSERT INTO `events` (`id`, `title`, `link`, `des`, `place`, `start_time`, `end_time`, `fb_id`) VALUES
(1, 'The Book Talk 2.0', 'the-book-talk-20', 'The Book Talk là một chương trình nói chuyện về sách được tổ chức tại Nhã Nam Thư quán với mục tiêu tạo nên một không gian chia sẻ giữa những người đọc sách về một chủ đề nhất định xoay quanh các chủ đề văn học. The Book Talk 1.0 tổ chức trong 2 năm 2014 & 2015 đã đi qua 8 chủ đề: \"Văn chương có ích gì?\", \"Ghiền sách mê phim\", \"Chúng ta nói gì khi nói về du ký\", \"Dung nhan của sách\", \"Sài Gòn, Sài Gòn\", \"Fantasy trong văn chương Việt\", \"Khi phái đẹp viết văn\" và \"Tại sao giải thưởng văn học\".<br />\n<br />\nNăm nay, The Book Talk quay trở lại với một phiên bản mới hơn. The Book Talk 2.0 tại NNTQ sẽ hướng tới tăng tối đa mức tranh luận trong chương trình, để The Book Talk trở thành diễn đàn mà Nhã Nam là người gợi đề tài và mọi người tham gia, bao gồm cả diễn giả và khán giả đều đưa lên được ý kiến của mình về đề tài được gợi ra. Tại The Book Talk 2.0, diễn giả cũng đơn thuần là một khán giả, và khán giả cũng có thể trở thành diễn giả. Để xóa nhòa khoảng cách ấy, The Book Talk sẽ tạo cơ hội để 1 bạn khán giả có thể cùng trở thành 1 diễn giả của chương trình. Và để tăng thêm tranh luận, The Book Talk sẽ đưa ra những vấn đề sát với thời sự và xã hội hơn.<br />\n<br />\nThời gian tổ chức: Chủ nhật tuần thứ 2 của các tháng 5, 7, 9, 11/2016 và tháng 1/2017 <br />\n<br />\nChủ đề đầu tiên của The Book Talk 2.0 đã được tổ chức vào ngày 15/5/2016  là: Triết học bước ra đường phố.', '{\"name\":\"Nh\\u00e3 Nam th\\u01b0 qu\\u00e1n\",\"location\":{\"city\":\"Ho Chi Minh City\",\"country\":\"Vietnam\",\"latitude\":10.8033082392720007902653378550894558429718017578125,\"longitude\":106.6749132629999934351872070692479610443115234375,\"street\":\"015 Lo B, chung cu 43 Ho Van Hue, Phu Nhuan\",\"zip\":\"84\"},\"id\":\"130382620419818\"}', '2017-06-25 02:30:00', '2017-06-30 03:00:00', '580221568822197'),
(2, 'Giao lưu với họa sĩ Tạ Huy Long - Lĩnh Nam Chích Quái', 'giao-luu-voi-hoa-si-ta-huy-long---linh-nam-chich-quai', '#Lĩnh_Nam_chích_quái - tập truyện ghi lại những chuyện kỳ lạ ở nước Nam là một danh tác văn học trung đại, một báu vật trong di sản văn hóa của ông cha. Ở #Lĩnh_Nam_chích_quái, những truyện tích thần kì được góp nhặt và ghi chép lại thể hiện quan niệm của ông cha về lịch sử dân tộc về phong tục tập quán, về cách đối nhân xử thế... #Lĩnh_Nam_chích_quái được viết bằng chữ Hán vào khoảng  đời Trần, qua thời gian được sự nhuận sắc, chỉnh sửa, bồi đắp cuả nhiều thế hệ các nhà nho Việt Nam. <br />\n<br />\nẤn bản #Lĩnh_Nam_chích_quái của Nhà xuất bản Kim Đồng là ấn bản giữ lại gần như nguyên vẹn bản dịch của các học giả Đinh Gia Khánh và Nguyễn Ngọc San. Đây cũng là ấn bản đầy đủ nhất về tư liệu, khảo cứu. Với hơn 200 tranh minh họa của họa sĩ Tạ Huy Long #Lĩnh_Nam_chích_quái là ấn bản đầu tiên có tranh minh họa màu tuyệt đẹp, vén bức màn quá khứ để phát quang ánh sáng của viên ngọc minh châu quý giá này. <br />\n<br />\nHơn 200 bức tranh minh họa trong #Lĩnh_Nam_chích_quái được vẽ tay hoàn toàn, được thực hiện kì công mô phỏng phong cách tranh khắc gỗ dân gian. Mỗi bức tranh gồm 2 bản, 1 bản nét đen, và từ 1 đến 2 bản màu. Một kì công thực sự của họa sĩ Tạ Huy Long, thể hiện tâm huyết và tài năng độc đáo của anh.<br />\n<br />\nTại sự kiện ra mắt sách #Lĩnh_Nam_chích_quái họa sĩ Tạ Huy Long sẽ chia sẻ thêm về những bức họa xuất sắc này.<br />\n???? Thời gian: 19:30 Chủ Nhật 11.6.2017<br />\n???? Địa điểm: Sân khấu trung tâm, Phố Sách Hà Nội<br />\n<br />\nTrân trọng!', '{\"name\":\"Hi\\u1ec7u s\\u00e1ch Kim \\u0110\\u1ed3ng - Ph\\u1ed1 s\\u00e1ch H\\u00e0 N\\u1ed9i\",\"location\":{\"city\":\"Hanoi\",\"country\":\"Vietnam\",\"latitude\":21.019741700000000861336957314051687717437744140625,\"longitude\":105.8561203999999946745447232387959957122802734375,\"street\":\"Ph\\u1ed1 19-12, Tr\\u1ea7n H\\u01b0ng \\u0110\\u1ea1o\"},\"id\":\"204980680011381\"}', '2017-06-11 12:30:00', '2017-06-11 14:00:00', '1181933245285447'),
(3, 'Hội sách gia đình - Tặng sách \"Người Mỹ giúp con ham đọc sách\"', 'hoi-sach-gia-dinh---tang-sach-nguoi-my-giup-con-ham-doc-sach', 'Các bậc cha mẹ thân mến!<br />\n<br />\nTháng 6 này Tủ sách Người Mẹ Tốt xin trân trọng giới thiệu chương trình \"Hội sách gia đình\", do Tủ sách Người Mẹ Tốt phối hợp cùng kênh thương mại điện tử Adayroi và Trường mầm non đặc biệt Myoko đồng tổ chức. <br />\nTrong sự kiện lần này, Adayroi xin được mở link tặng sách miễn phí cho các bậc cha mẹ đăng kí và tham dự trực tiếp tại chương trình. <br />\n<br />\nCác bậc cha mẹ khi tham gia chương trình không cần đem theo giấy tờ gì, không mất bất cứ chi phí tham dự nào đâu ạ. <3 Sự kiện lần này hoàn toàn miễn phí, các cha mẹ ở tỉnh xa hoặc bận rộn cũng có thể nhờ người đến lấy sách tại chương trình.<br />\n<br />\nLink đăng kí nhận sách: https://goo.gl/dsTw3K<br />\nĐịa điểm: Cafe sách Đông Tây, 99 Ngụy Như Kon Tum - Thanh Xuân - Hà Nội (Gửi xe tại cổng Ngụy Như Kon Tum - làng sinh viên Hacinco)<br />\nThời gian: 9h - 11h ngày Chủ nhật - 11/06/2017<br />\n<br />\n☎ Hotline hỗ trợ thông tin và giải đáp thắc mắc: 0965 974 488', '{\"name\":\"Cafe S\\u00e1ch \\u0110\\u00f4ng T\\u00e2y\",\"location\":{\"city\":\"Hanoi\",\"country\":\"Vietnam\",\"latitude\":21.00159696838699829868346569128334522247314453125,\"longitude\":105.8028850422600015690477448515594005584716796875,\"street\":\"99 Ng\\u1ee5y Nh\\u01b0 Kon Tum\",\"zip\":\"04\"},\"id\":\"898301696931544\"}', '2017-06-11 02:00:00', '2017-06-11 04:00:00', '1143378262432744'),
(4, 'Hội thảo giới thiệu sách \"Ngẫu nhiên và Tất yếu\" - Jacques Monod', 'hoi-thao-gioi-thieu-sach-ngau-nhien-va-tat-yeu---jacques-monod', 'Trân trọng mời bạn đọc tham gia:<br />\n<br />\nHội thảo giới thiệu sách: NGẪU NHIÊN VÀ TẤT YẾU<br />\n<br />\n- 18:00, thứ Ba, ngày 06/06/2017, Hội trường l’Espace, 24 Tràng Tiền, Hà Nội.<br />\n- 18 :00, thứ Ba, ngày 13/06/2017, Thư viện Idecaf, 28 Lê Thánh Tôn, Quận 1, Tp.HCM.<br />\n<br />\nTác phẩm \"Ngẫu nhiên và tất yếu\" (Le hasard et la nécessité) được xuất bản năm 1970. Từ những khám phá mới nhất về sinh vật học phân tử, tác giả Jacques Monod đề xuất một cách nhìn triết học mới về sự tiến hoá của con người và xã hội. <br />\n<br />\nCó thể phát biểu niềm tin vững chắc nhất của tác giả và của đại đa số các nhà khoa học thời nay như một định đề cơ bản : Tự nhiên là khách thể và không mang dự phóng. Tự nhiên hiện hữu bên ngoài ý thức con người, và sự hiện hữu đó không mang theo bất cứ mục đích nào. Từ đó, khác với các học thuyết được ông đánh giá là “hữu linh”, vì đều dựa trên giả thuyết (hiển ngôn hay nằm sâu trong nền tảng của chúng) cho rằng tự nhiên có “ý chí” hay “dự định” (như của Hegel, Marx, Engels, Bergson, Teilhard de Chardin, v.v.), Monod quan niệm rằng sự sống, cũng như con người, xuất hiện là do ngẫu nhiên thuần tuý. Nhưng trong khi đó toàn thể tự nhiên bị chi phối bởi tính tất yếu trong các quy luật vật lý.<br />\n<br />\nTại sao sự tiến hoá của sinh quyển trên trái đất rõ ràng là hướng thượng và tiến tới những hình thức ngày càng phức tạp ? Với các học thuyết cổ điển, dự phóng hướng thượng là hiển nhiên. Nhưng với tác giả câu hỏi cơ bản đó là một vấn đề triết học hoàn toàn mới và rất nan giải. Đây là vấn đề mà tác giả phải giải quyết trong tác phẩm này, tuyệt đối chỉ bằng những quy luật vật lý của tự nhiên, khách thể và không mang dự phóng. Nội dung khoa học của tác phẩm chiếm phần trọng tâm qua hơn nửa số chương, trình bày cả một quá trình tiến hoá từ những phân tử sinh học đơn giản nhất cho tới ý thức con người và cuối cùng là ý thức xã hội. Đây cũng là một giải thích khoa học rất phong phú về sự kết hợp tinh tế của hai yếu tố ngẫu nhiên và tất định.<br />\n<br />\nVới quan niệm như thế về tự nhiên, con người và xã hội, làm sao chúng ta có thể đặt nền tảng cho đạo lý ? Để kết thúc tác phẩm, tác giả nói tại sao vấn đề còn mở ngỏ : những niềm tin siêu hình đã nảy sinh qua nhiều thế kỷ đang mất dần, để chỉ còn lại nhận định sau : \"đạo lý của hiểu biết cũng là sự hiểu biết về đạo lý\". Từ nay sẽ không có một đấng cứu rỗi nào, con người chỉ có thể lấy thái độ khách quan khoa học làm nền tảng cho văn hoá hiện đại để có thể đứng trước sự chọn lựa hoàn toàn có ý thức và tự do giữa \"Vương quốc của thiên đường và địa ngục\". <br />\n<br />\nDiễn giả:<br />\nDịch giả Hà Dương Tuấn đã học cao học toán tại đại học Paris, là chuyên gia công nghệ thông tin trong hãng Ericsson trước khi về hưu. Ông đã dịch cuốn \"Sự hình thành tinh thần khoa học\" của tác giả Bachelard, cũng do NXB Tri Thức xuất bản.<br />\nGS Chu Hảo : Giám đốc NXBTT.', '{\"name\":\"Trung t\\u00e2m V\\u0103n h\\u00f3a Ph\\u00e1p t\\u1ea1i H\\u00e0 N\\u1ed9i - L\'Espace\",\"location\":{\"city\":\"Hanoi\",\"country\":\"Vietnam\",\"latitude\":21.025055438100000770873521105386316776275634765625,\"located_in\":\"649006508443196\",\"longitude\":105.856013697999998157683876343071460723876953125,\"street\":\"24 Tra\\u0300ng Ti\\u00ea\\u0300n\",\"zip\":\"100\"},\"id\":\"242078635893289\"}', '2017-06-06 11:00:00', '2017-06-13 13:00:00', '1771664546478913'),
(5, 'Chào hè - Tháng sách Kim Đồng - Join nhận quà', 'chao-he---thang-sach-kim-dong---join-nhan-qua', 'Chào Hè, chào một mùa hè tưng bừng, tưng bừng và thật tưng bừng.<br />\n<br />\n 12 Nhà sách trên khắp cả nước sẽ áp dụng chính sách ưu đãi chào đón MÙA HÈ 2017 Phiêu-lưu-trên-từng-trang-sách <3 <3 <3<br />\n<br />\nTừ 27.5 đến hết 30.6 trên hệ thống Nhà sách Kim Đồng<br />\nƯu đãi 15%, 30%, 50% <br />\nNhiều quà tặng hấp dẫn!<br />\n<br />\n Bút bi Kim Đồng : với tất cả đơn hàng<br />\n<br />\n Móc khóa kỉ niệm 60 năm thành lập XNB Kim Đồng: với bill thanh toán từ 100,000đ trở lên và với đơn hàng có 1 cuốn sách kỉ niệm 60 năm.<br />\n<br />\n Sổ Dragon Ball hoặc One Piece (A6) : với bill thanh toán từ 200,000đ trở lên.<br />\n<br />\n Sổ Dragon Ball hoặc One Piece (A5): với bill thanh toán từ 400.000 đ trở lên.<br />\n<br />\n Combo Sách + Sổ + Túi Cẩm nang mùa hè: với bill thanh toán từ 700,000đ  trở lên.<br />\n<br />\n Áo phông Dragon Ball Z hoặc One Piece với bill thanh toán từ 1.000.000 đ trở lên.<br />\n<br />\nĐặc biệt tại 62 Bà Triệu, Hà Nội, chương trình bigsale Chào Hè được chờ đón nhất trong năm sẽ trở lại với list sách phong phú phù hợp với mọi đối tượng và nhu cầu. <br />\n<br />\nMột mùa hè thênh thang năng gió, một mùa hè kích thích trí tưởng tượng. bạn mơ về một vùng đất kì thú, bạn mơ về những chuyến phiêu lưu, muốn đọc những câu chuyện kỳ lạ vượt trên cái bình thường hàng ngày. Bạn muốn trải nghiệm những cảm xúc, muốn tư duy về cuộc sống.... ba tháng hè luôn là không đủ cho những khát khao.<br />\n<br />\nHãy bước vào mỗi trang sách, chỉ có thế giới sách nhiệm màu là không giới hạn bạn, chắp thêm cho bạn đôi cánh xuyên không và xuyên thời gian, bạn sẽ được trải nghiệm nhiều nhất và sâu nhất mỗi phút đọc sách...<br />\n<br />\nTháng Sáu này, trên giá sách của Nhà xuất bản Kim Đồng xuất hiện nhiều tựa sách mới, được trông chờ: Đó là bộ sưu tập những ấn bản kỉ niệm 60 năm thành lập Nhà xuất bản Kim Đồng: artbook Lĩnh Nam chích quái, Các vị vua trẻ trong sử Việt, Biên niêm sử tehes giới bằng hình, Bộ tinh tuyển 6 cuốn tranh truyện dân gian màu (Tấm Cám, Thạch Sanh, Bánh chưng bánh dày, ...), Tý Quậy-  Những truyện siêu buồn cười, Các tác phẩm văn học kinh điển nước ngoài với minh họa của họa sĩ Việt Nam....<br />\n<br />\nPS: Anh Mắc chỉ cho max sự kiện là 14 ngày trong khi sự kiện của Kim Đồng dài hơn cả 4 tuần bạn nhé ????<br />\n <br />\n✍ Nhớ các chi tiết chính<br />\n???? Từ 27.5 đến hết 30.6 trên hệ thống Nhà sách Kim Đồng<br />\n???? Ưu đãi 15%, 30%, 50% <br />\n???? Nhiều quà tặng hấp dẫn!<br />\n<br />\nJoint sự kiện nhận thông tin và quà tặng bạn nhé! <br />\nChia sẻ thông tin cho bạn bè mình cùng biết, cùng đi và cùng vui!', '{\"name\":\"NXB Kim \\u0110\\u1ed3ng\",\"location\":{\"city\":\"Hanoi\",\"country\":\"Vietnam\",\"latitude\":21.017610499999999973397279973141849040985107421875,\"longitude\":105.8571625000000011596057447604835033416748046875,\"street\":\"22 H\\u00e0ng Chu\\u1ed1i\",\"zip\":\"84\"},\"id\":\"99839463868\"}', '2017-05-27 01:00:00', '2017-06-09 14:00:00', '179279425933229');
